html:
{% set employee = frappe.get_doc("Employee", doc.employee) %}
{% set identities = frappe.get_list("Identity", filters={"parent": employee.name},
    fields=["nationality", "id_number", "issue_place", "issue_date_h"]) %}
{% if identities %}
{% set identity = identities[0] %}
{% else %}
{% set identity = [] %}
{% endif %}
{% set date = frappe.utils.getdate(doc.date_g).month %}
{% set salary_slips = frappe.db.sql("""select name from `tabSalary Slip` where employee='{0}' and (MONTH(start_date) = {1} or MONTH(start_date) < {1} ) limit 1""".format(employee.name, date), as_dict=True) %}
{% if salary_slips %}
{% set ss = frappe.get_doc("Salary Slip", salary_slips[0].name) %}
{% else %}
{% set ss = {} %}
{% endif %}
{% if doc.show_letterhead %}
{{letter_head}}
{% endif %}

<div class="row">
    <!-- ================ EN ================ -->
    {% if doc.language == 'English' or doc.language == 'Both' %}
    <div class="text-left {% if doc.language == 'English' %} col-xs-12 {% else %} col-xs-6 {% endif %}">
        <p>Date : {{ doc.date_g }}</p>
        <p>Corresponding to : {{ doc.date_h }}</p>
        <p>Subject : {{ get_translation(doc.purpose)}}</p>
    </div>
    {% endif %}

    <!-- ================ AR ================ -->
    {% if doc.language == 'Arabic' or doc.language == 'Both' %}
        <div class="text-right {% if doc.language == 'Arabic' %} col-xs-12 {% else %} col-xs-6 {% endif %}">
            <p>التاريخ : {{ doc.date_g }}</p>
            <p>الموافق : {{ doc.date_h }}</p>
            <p>الموضوع : {{ get_translation(doc.purpose, "ar")}}</p>
        </div>
    {% endif %}
</div>

<div class="row">
    {% if doc.language == 'English' or doc.language == 'Both' %}
    <!-- ================ EN ================ -->
    <div class="text-left {% if doc.language == 'English' %} col-xs-12 {% else %} col-xs-6 {% endif %}">
        <h2>{{ get_translation(doc.type)}}</h2>
    </div>
    {% endif %}

    <!-- ================ AR ================ -->
    {% if doc.language == 'Arabic' or doc.language == 'Both' %}
    <div class="text-right {% if doc.language == 'Arabic' %} col-xs-12 {% else %} col-xs-6 {% endif %}">
        <h2>{{ get_translation(doc.type, "ar")}}</h2>
    </div>
    {% endif %}
</div>
<div class="row section-break">
    <hr>
</div>
<div class="row">
    {% if doc.language == 'English' or doc.language == 'Both' %}
    <!-- ================ EN ================ -->
    <div class="text-left {% if doc.language == 'English' %} col-xs-12 {% else %} col-xs-6 {% endif %}">
        <h4>For {{ get_translation(doc.letter_to) or ""}}</h4>
        <p>We hereby certify that {% if employee.gender_ == 'Male' %} {{ get_translation("Mr.", "ar") }} {% elif employee.gender_ == 'Female' %} {{ get_translation("Ms.", "ar") }} {% endif %} 
             {{ employee.full_name_en or get_translation(employee.first_name) }}</p>
        <p>{{ get_translation(identity.nationality) or "" }} Nationality</p>
        <p>holder of ID ({{ identity.id_number or "" }}) issued in ({{ get_translation(identity.issue_place) or "" }}) on ({{ identity.issue_date_h or "" }}) H has been working for our company</p>
        <p> in position of {{ get_translation(employee.designation) or ""}}</p>
        <p>since {{ employee.date_of_joining or ""}} and still employed as of the date of this letter .</p>
        {% if doc.show_salary %}
            <p>His total salary is ({{frappe.utils.fmt_money(ss.get("net_pay", 0), currency=ss.currency)}}) per month, {{ss.get("total_in_words", "")}} </p>
        {% endif %}
        {% if doc.show_salary_details %}
            <p>And the details of the monthly salary is : </p>
            {% for row in ss.earnings %}
            <p>{{row.salary_component}} : {{frappe.utils.fmt_money(row.amount, currency=ss.currency)}} monthly</p>
            {% endfor %}
            {% for row in ss.deductions %}
            <p>{{row.salary_component}} : {{frappe.utils.fmt_money(row.amount, currency=ss.currency)}} monthly</p>
            {% endfor %}
        {% endif %}

        <p>This letter has been issued upon the request of the employee For {{doc.purpose}}</p>
        <p>takes no responsibility for any action as a result of this letter.</p>
    </div>
    {% endif %}

    <!-- ================ AR ================ -->
    {% if doc.language == 'Arabic' or doc.language == 'Both' %}

    <div class="text-right {% if doc.language == 'Arabic' %} col-xs-12 {% else %} col-xs-6 {% endif %}">
        <h4>الى {{ get_translation(doc.letter_to, "ar")}} </h4>
        <p>نفيدكم نحن {{ get_translation(employee.company, "ar") }} بأن</p>
        <p> , السيد / {{ get_translation(doc.employee_name, "ar") }}</p>
        <p>{{ get_translation(identity.nationality, "ar") or ""}} الجنسية حامل رقم ({{identity.id_number or ""}})</p>
        <p> والصادرة من ({{_(identity.issue_place) or ""}}) بتاريخ ({{identity.issue_date_h or ""}}) هـ<br> يعمل لدينا بمهنة {{ get_translation(employee.designation, "ar") or ""}}</p>
        <p>منذ {{ employee.date_of_joining or ""}} م</p>
        <p>ولا زال المذكور على رأس عمله حتى تاريخه</p>
        {% if doc.show_salary %}
        <p>علماً بأنه يتقاضى راتباً إجمالياً وقدره ({{frappe.utils.fmt_money(ss.get("net_pay", 0), currency=ss.currency)}}),  {{_(ss.get("total_in_words", ""))}}</p>
        {% endif %}

        {% if doc.show_salary_details %}
        <p> : و تفاصيل الراتب هي</p>
            {% for row in ss.earnings %}
            <p>{{ get_translation(row.salary_component, "ar") }} : {{frappe.utils.fmt_money(row.amount, currency=ss.currency)}} شهرياً</p>
            {% endfor %}
            {% for row in ss.deductions %}
            <p>{{ get_translation(row.salary_component, "ar") }} : {{frappe.utils.fmt_money(row.amount, currency=ss.currency)}} شهرياً</p>
            {% endfor %}
        {% endif %}
        <p>{{ get_translation(doc.purpose, "ar") }} و لقد أعطي هذا الخطاب بناءً على طلبه لغرض</p>
        <p>. دون أدنى مسئولية مالية أو قانونية على المؤسسة / الشركة</p>
    </div>
    {% endif %}

</div>
<div class="row section-break">
    <hr>
</div>
{% set signatures = frappe.get_list("Signatures", filters={"parent": employee.company}, fields=["name1", "job_title", "idx"])%}
<div class="row">
    <div class="col-xs-12">
        <table class="table" dir="rtl">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col" style="text-align: right; width:30%;">الاسم</th>
                <th scope="col" style="text-align: right; width:30%;">الاسم الوظيفي</th>
                <th scope="col" style="text-align: center; width:30%;">التوقيع</th>
              </tr>
            </thead>
            <tbody>
            {% if signatures %}
                {% for row in signatures %}
                <tr>
                    <th scope="row">{{row.idx}}</th>
                    <td>{{frappe.db.get_value("Employee", row.name1, "first_name")}}</td>
                    <td>{{row.job_title or ""}}</td>
                    <td></td>
                </tr>
                {% endfor %}
            {% endif %}
            </tbody>
          </table>
    </div>
</div>
<!--=============== Footer ================ -->
{% if doc.footer %}
<div class="row section-break">
    <hr>
</div>
<div class="row">
    <div class="col-xs-12 text-center">
        {{ doc.footer }}
    </div>
</div>
{% endif %}
